def solve(arg, init_i):
    i = init_i
    while True:
        a = arg[i-init_i : i]
        s = set(a)
        if len(s) == init_i:
            break
        i += 1
    return i


def main():
    print(solve(day_input(), 4))
    print(solve(day_input(), 14))


